import build from './build';
import { terser } from 'rollup-plugin-terser';
import bundleSize from 'rollup-plugin-bundle-size';

const RESERVED = [
  // general
  'Virchual',

  // events
  'mounted',
  'move',
  'destroy',
  'dragstart',
  'drag',
  'dragend',
  'next',
  'prev',
  'register',
];

export default Object.assign(build, {
  plugins: build.plugins.concat([
    terser({
      compress: {
        drop_console: true,
        keep_fargs: false,
        passes: 2,
      },
      keep_fnames: false,
      ecma: 6,
      mangle: {
        properties: {
          keep_quoted: true,
          builtins: false,
          reserved: RESERVED,
        },
        toplevel: false,
      },
    }),
    bundleSize(),
  ]),
});
